import react, { useEffect, useState, useRef } from "react";
import PeopleIcon from "@material-ui/icons/People";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import Room from "./Room";
//use for time
import moment from "moment";
import io from "socket.io-client";
import Peer from "simple-peer";

function MeetingHeader({ setMessenger, id, isAdmin, peers, userVideo }) {
	const formatDate = () => {
		return moment().format("LT");
	};

	const [currentTime, setCurrentTime] = useState(() => {
		formatDate();
	});
	useEffect(() => {
		setInterval(() => setCurrentTime(formatDate()), 1000);
		console.log(currentTime);
	}, []);

	const vidStyle = {
		// height: "calc(50vh -90px)",
		// borderRadius:"10px"
		height: "45%",
		width: "45%",

		padding: "10px",
		paddingBottom: "5px",
	};
	const ref = useRef();

	const Video = (props) => {
		console.log("VIDEO CALLED");
		useEffect(() => {
			console.log(props.peer);
			props.peer.on("stream", (stream) => {
				console.log("ENTE0RD" + stream);
				ref.current.srcObject = stream;
			});
		}, []);
		console.log(ref);
		return <video style={vidStyle} autoPlay ref={ref} />;
	};

	const Room = () => {
		console.log("ROOM CALLED");
		return (
			<div className="container">
				{console.log(userVideo)}
				<video
					style={vidStyle}
					muted
					ref={userVideo}
				
					autoPlay
				/>
				{peers.map((peer, index) => {
					return <Video key={index} peer={peer} />;
				})}
			</div>
		);
	};

	return (
		<div>
			<Room />

			<div class="frame-header">
				<div class="header-items icon-block">
					<PeopleIcon />
				</div>
				<div
					class="header-items icon-block"
					onClick={() => {
						setMessenger(true);
					}}
				>
					<ChatBubbleIcon />
				</div>
				<div class="header-items date-block">{currentTime}</div>
				<div class="header-items icon-block">
					<AccountCircleIcon />
				</div>
			</div>
		</div>
	);
}

export default MeetingHeader;
