import react, { useEffect, useState, useRef } from "react";
import PeopleIcon from "@material-ui/icons/People";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
//use for time
import moment from "moment";
import styled from "styled-components";

function MeetingHeader({ setMessenger, peers, userVideo }) {
	const Container = styled.div`
		display: flex;
		align-items: center;
		height: calc(100vh - 90px);
		width: 100vw;
		flex-wrap: wrap;
		justify-content: center;
	`;

	const StyledVideo = styled.video`
		height: 50%;
		width: 50%;
	`;

	const Video = (props) => {
		const ref = useRef();

		useEffect(() => {
			console.log(props.peer);
			props.peer.on("stream", (stream) => {
				console.log(stream);
				ref.current.srcObject = stream;
			});
		}, []);

		return <StyledVideo playsInline autoPlay ref={ref} />;
	};

	const formatDate = () => {
		return moment().format("LT");
	};

	const [currentTime, setCurrentTime] = useState(() => {
		formatDate();
	});
	useEffect(() => {
		setInterval(() => setCurrentTime(formatDate()), 1000);
		console.log(currentTime);
	}, []);

	return (
		<div>
			{/* <video class="video-container"></video> */}

			<Container>
				<StyledVideo muted ref={userVideo} autoPlay playsInline />
				{peers.map((peer, index) => {
					return <Video key={index} peer={peer} />;
				})}
			</Container>

			<div class="frame-header">
				<div class="header-items icon-block">
					<PeopleIcon />
				</div>
				<div
					class="header-items icon-block"
					onClick={() => {
						setMessenger(true);
					}}
				>
					<ChatBubbleIcon />
				</div>
				<div class="header-items date-block">{currentTime}</div>
				<div class="header-items icon-block">
					<AccountCircleIcon />
				</div>
			</div>
		</div>
	);
}

export default MeetingHeader;
