import react, {
	useState,
	useEffect,
	useReducer,
	useRef,
	createRef,
} from "react";

import { useParams, useHistory } from "react-router-dom";
import MeetingHeader from "./meetingHeader";
import MeetingFooter from "./meetingFooter";
import MeetingInfo from "./meetingInfo";
import styled from "styled-components";

import Messenger from "./messenger";
import Alert from "./alert";
import Peer from "simple-peer";
import io from "socket.io-client";
function VideoPage() {
	const isAdmin = window.location.hash === "#init" ? true : false;

	//#init
	// console.log(window.location.hash);

	const { id } = useParams();

	// let peer = null;
	const url = `${window.location.origin}${window.location.pathname}`;

	const [meetingInfoPopUp, setMeetingInfoPopUp] = useState(false);
	const [isMessenger, setMessenger] = useState(false);

	useEffect(() => {
		if (isAdmin) {
			setMeetingInfoPopUp(true);
		}
	}, []);

	const vidStyle = {
		// height: "calc(50vh -90px)",
		// borderRadius:"10px"
		height: "45%",
		width: "45%",

		padding: "10px",
		paddingBottom: "5px",
	};

	const Video = (props) => {
		const ref = useRef();

		useEffect(() => {
			console.log(props.peer);
			props.peer.on("stream", (stream) => {
				console.log("ENTE0RD" + stream);
				ref.current.srcObject = stream;
			});
		}, []);

		return <video style={vidStyle} autoPlay ref={ref} />;
	};

	const videoConstraints = {
		height: window.innerHeight / 2,
		width: window.innerWidth / 2,
	};

	const Room = (props) => {
		const numUsers = useRef();
		const [peers, setPeers] = useState([]);
		const socketRef = useRef();
		const userVideo = createRef({});
		const peersRef = useRef([]);
		const roomID = props.id;

		useEffect(() => {
			socketRef.current = io.connect("http://localhost:9000");
			navigator.mediaDevices
				.getUserMedia({ video: videoConstraints, audio: true })
				.then((stream) => {
					console.log("USERVIDEO" + stream);
					userVideo.current.srcObject=()=>{return stream}              
					// userVideo.current.srcObject = stream;
					// let video = document.getElementById("video");

					// if ("srcObject" in video) {
					// 	video.srcObject = stream;
					// } else {
					// 	video.src = window.URL.createObjectURL(stream); // for older browsers
					// }

					// video.play();
					// LOGIC THAT USER HAS JOINED THE ROOM

					//THIS EVENT Is NOT CACHED AT BACKEND
					//.emit means sending  to backend
					socketRef.current.emit("JOINED ROOM");
					socketRef.current.emit("join room", roomID);
					// Recieve users from backened
					console.log(isAdmin);
					// if (!isAdmin) {
					socketRef.current.on("all users", (users) => {
						//peers is for how many videos are rendering
						console.log(users.length);
					
						const peersForVideo = [];
						users.forEach((userID) => {
							console.log(userID + " USER ID OF USER IN THE ROOM ");
							console.log(socketRef.current);
							//socketRef.current.id is the of user currently joined
							// UserID  id's of all those inside the meeting

							const peer = createPeer(userID, socketRef.current.id, stream);
							//peersRef is for which is having connection with which
							peersRef.current.push({
								peerID: userID,
								peer,
							});

							peersForVideo.push(peer);
						});
						setPeers(peersForVideo);
						console.log(peers);
					});
					//PERSON IN THE ROOM GETS NOTIFIED THAT SOMEBODY HAS JOINED
					//.on means recieving from backend
					socketRef.current.on("user joined", (payload) => {
						const peer = addPeer(payload.signal, payload.callerID, stream);

						peersRef.current.push({
							peerID: payload.callerID,
							peer,
						});
						setPeers((users) => [...users, peer]);
					});
					socketRef.current.on("receiving returned signal", (payload) => {
						// signal has been send to multiple now multiple users are sending back the signal to caller
						const item = peersRef.current.find((p) => p.peerID === payload.id);
						console.log(item);
						item.peer.signal(payload.signal);
					});
					// }
				});
		}, []);
		function createPeer(userToSignal, callerID, stream) {
			console.log("CREATE PEER");
			const peer = new Peer({
				initiator: true,
				//trickle wait for all the data to send makes it slow
				trickle: false,
				stream,
			});
			//generates signal
			//sending to backend
			peer.on("signal", (signal) => {
				socketRef.current.emit("sending signal", {
					userToSignal,
					callerID,
					signal,
				});
			});
			return peer;
		}

		function addPeer(incomingSignal, callerID, stream) {
			console.log("Add Peer");
			const peer = new Peer({
				initiator: false,
				trickle: false,
				stream,
			});
			// 1 accepting the incoming signal that this will return the signal
			peer.signal(incomingSignal);

			peer.on("signal", (signal) => {
				socketRef.current.emit("returning signal", { signal, callerID });
			});
			return peer;
		}

		// console.log("PEERS " + JSON.stringify(peers));
		return (
			<div className="container">
				<video id="video" style={vidStyle} muted ref={userVideo} autoPlay />
				{peers.map((peer, index) => {
					console.log(index);
					return <Video key={index} peer={peer} />;
				})}
			</div>
		);
	};

	return (
		<div class="videoScreen">
			<MeetingHeader
				setMessenger={setMessenger}
				Room={Room}
				isAdmin={isAdmin}
				id={id}
			/>
			<MeetingFooter />
			{isAdmin && meetingInfoPopUp && (
				<MeetingInfo url={url} setMeetingInfoPopUp={setMeetingInfoPopUp} />
			)}
			{isMessenger ? (
				<Messenger
					setMessenger={setMessenger}
					// messageList={messageList}
				/>
			) : (
				<Alert />
			)}
		</div>
	);
}

export default VideoPage;
