import react, { useState, useEffect } from "react";


import CloseIcon from "@material-ui/icons/Close";
import PersonIcon from "@material-ui/icons/Person";
import FileCopyIcon from "@material-ui/icons/FileCopy";
import SecuritySharpIcon from "@material-ui/icons/SecuritySharp";
import { useParams } from "react-router-dom";
import MeetingHeader from "./meetingHeader";

function VideoPage() {
  console.log(window.location.hash);
  const { id } = useParams();
  const isAdmin = window.location.hash === "#init" ? true : false;
  const url = `${window.location.origin}${window.location.pathname}`;
  const [meetingInfoPopUp, setMeetingInfoPopUp] = useState(false);

  useEffect(() => {
      //Call every time when page renders
    if (isAdmin) {
      setMeetingInfoPopUp(true);
    }
  }, []);

  const handleCloseButtonClick=()=>{
      setMeetingInfoPopUp(false)
  }

  console.log(isAdmin);
  return (
    <div class="videoScreen">
      <MeetingHeader/>

      {isAdmin && meetingInfoPopUp && (
        <div class="meeting-info-block">
          <div class="meeting-header">
            <h3>Your Meeting's ready</h3>
            <CloseIcon onClick={handleCloseButtonClick}/>
          </div>
          <button class="add-people-btn">
            <PersonIcon />
            Add others
          </button>
          <p class="info-text">
            Or share this meeting link with others you want in the meeting
          </p>
          <div class="meet-link">
            <span>{url}</span>
            <FileCopyIcon />
          </div>
          <div class="permission-text">
            <SecuritySharpIcon />
            <p class="small-text">
              People who use this meeting link must get your permission before
              they can join.
            </p>
          </div>
          <p class="small-text">Joined as akshay@gmail.com</p>
        </div>
      )}
    </div>
  );
}

export default VideoPage;
