import react, { useEffect, useState, useRef } from "react";
import PeopleIcon from "@material-ui/icons/People";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
//use for time
import moment from "moment";
import styled from "styled-components";

function MeetingHeader({ setMessenger,peers }) {
  
  var video = document.querySelector("video");
  console.log(peers);
  peers.map((peer)=>{
    peer.on('stream',stream=>{
      console.log(stream);
      //  if ("srcObject" in video) {
      //    video.srcObject = stream;
      //  } else {
         video.src = window.URL.createObjectURL(stream); // for older browsers
      //  }
       video.play();
    })
  })
   
   
    
  const formatDate = () => {
    return moment().format("LT");
  };

  const [currentTime, setCurrentTime] = useState(() => {
    formatDate();
  });
  useEffect(() => {
    setInterval(() => setCurrentTime(formatDate()), 1000);
    console.log(currentTime);
  }, []);

  return (
    <div>
      <video class="video-container" src=""></video>
      {/* <video class="video-container" controls src=""></video> */}

      {/* {peers.length !== 0 ? (
        <Container class="video-container">
          <StyledVideo muted ref={userVideo} autoPlay playsInline />
          {peers.map((peer, index) => {
            return <Video key={index} peer={peer} />;
          })}
        </Container>
      ) : (
        <video class="video-container" ref={userVideo}></video>
      )} */}

      <div class="frame-header">
        <div class="header-items icon-block">
          <PeopleIcon />
        </div>
        <div
          class="header-items icon-block"
          onClick={() => {
            setMessenger(true);
          }}
        >
          <ChatBubbleIcon />
        </div>
        <div class="header-items date-block">{currentTime}</div>
        <div class="header-items icon-block">
          <AccountCircleIcon />
        </div>
      </div>
    </div>
  );
}

export default MeetingHeader;
