import react, { useEffect, useState } from "react";
import PeopleIcon from "@material-ui/icons/People";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import moment from "moment";
function MeetingHeader({ setMessenger }) {
  let interval = null;

  const formatDate = (timestamp) => {
    return moment(timestamp).format("h:mm A");
  };

  const [currentTime, setCurrentTime] = useState(() => {
    return formatDate();
  });
  useEffect(() => {
    interval = setInterval(() => setCurrentTime(formatDate()), 1000);
    console.log(currentTime);
    return () => {
      clearInterval(interval);
    };
  }, []);
  return (
    <div>
      <video class="video-container" controls></video>
      <div class="frame-header">
        <div class="header-items icon-block">
          <PeopleIcon />
        </div>
        <div
          class="header-items icon-block"
          onClick={() => {
            setMessenger(true);
          }}
        >
          <ChatBubbleIcon />
        </div>
        <div class="header-items date-block">{currentTime}</div>
        <div class="header-items icon-block">
          <AccountCircleIcon />
        </div>
      </div>
    </div>
  );
}

export default MeetingHeader;
